def city_all_name():
    soup = BeautifulSoup(html, 'lxml')
    print(type(soup))
    # find_all = soup.find_all('div', {'class': 'city_item_container'})
    find_all = soup.find_all('div', {'class': 'city_char_row'})
    city_dict = {}
    for zimu in find_all:
        # zi_mu = BeautifulSoup(zimu, 'lxml')
        zi_mu = zimu.div.text
        print(zi_mu)

        city_name = zimu.find_all('div', {'class': 'city_button'})
        city_list = []
        for x in city_name:
            print(x)
            sheng = x.attrs['title']
            print(sheng)
            city = x.text
            print(city)
            city_list.append(city)
            # city_dict.pop
        city_dict[zi_mu] = city_list
    print(city_dict)


